  and table_id = #{tableId,jdbcType=INTEGER}

mapper.update:
update qrcode_table


        table_status = #{tableStatus,jdbcType=INTEGER},


        create_time = #{createTime,jdbcType=TIMESTAMP},

    now()

where qrcode_id = #{qrcodeId,jdbcType=INTEGER}
and table_id = #{tableId,jdbcType=INTEGER}

update qrcode_table
set table_status = #{tableStatus,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = now()
